// src/theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#ffae00', // This sets the primary color to orange
    },
    background: {
      default: '#1a1a1a',
      paper: '#333333',
    },
    text: {
      primary: '#ffffff',
      secondary: '#cfd8dc',
    },
  },
  typography: {
    h1: {
      fontSize: '2.5rem',
      fontWeight: 700,
    },
    h2: {
      fontSize: '2rem',
      fontWeight: 600,
    },
    body1: {
      fontSize: '1rem',
    },
  },
});

export default theme;
