// src/components/ParticlesBackground.js
import React from 'react';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import styled from 'styled-components';

const ParticlesContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
`;

const ParticlesBackground = () => {
  // Initialize the tsParticles instance with all available features
  const particlesInit = async (engine) => {
    // Load the full tsparticles package to use all features
    await loadFull(engine);
  };

  // Optional: Customize particle options or load them from a separate file
  const particlesOptions = {
    background: {
      color: {
        value: '#1a1a1a', // Change background color as needed
      },
    },
    fpsLimit: 60,
    interactivity: {
      events: {
        onClick: {
          enable: true,
          mode: 'push', // Action on click
        },
        onHover: {
          enable: true,
          mode: 'repulse', // Action on hover
        },
        resize: true,
      },
      modes: {
        push: {
          quantity: 4, // Number of particles to add on click
        },
        repulse: {
          distance: 200, // Distance for repulsion effect
          duration: 0.4,
        },
      },
    },
    particles: {
      color: {
        value: '#ffae00', // Particle color
      },
      links: {
        color: '#ffffff', // Link color
        distance: 150, // Link distance
        enable: true,
        opacity: 0.5,
        width: 1,
      },
      collisions: {
        enable: false,
      },
      move: {
        direction: 'none',
        enable: true,
        outModes: {
          default: 'bounce', // Behavior when particles reach edge
        },
        random: false,
        speed: 2,
        straight: false,
      },
      number: {
        density: {
          enable: true,
          area: 800, // Density area
        },
        value: 80, // Number of particles
      },
      opacity: {
        value: 0.5, // Opacity of particles
      },
      shape: {
        type: 'circle', // Shape of particles
      },
      size: {
        value: { min: 1, max: 5 }, // Size range of particles
      },
    },
    detectRetina: true,
  };

  return (
    <ParticlesContainer>
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={particlesOptions}
      />
    </ParticlesContainer>
  );
};

export default ParticlesBackground;
